var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppBankFundTransferNew',
    Heading:'AppBankFundTransfer',
    BankFundTransferId:{label :'BankFundTransferId'},
    FromAccountNo:{label :'FromAccountNo'},
    ToAccountNo:{label :'ToAccountNo'},
    Date:{label :'Date'},
    ReferenceNo:{label :'ReferenceNo'},
    Amount:{label :'Amount'},
    }

router.get('/', sessionChecker, function(req, res, next) {
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
        statusCode:100,
        statusMessage:'',        
        record:{bankFundTransferId:'',
        fromAccountNo:'',
        toAccountNo:'',
        date:'',
        referenceNo:'',
        amount:'',
        }
    }
    res.render('AppBankFundTransferNewPage',parameter);
    console.log(parameter);
});
router.post('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.body;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }    
    dao.newAppBankFundTransfer(jsonObj,function(err,rows) {
        if(err==null){
            parameter.statusCode = 200;
            parameter.statusMessage = 'Successfully created';
        }else{
            parameter.statusCode = err.code;
            parameter.statusMessage = err.message;
        }
        parameter.record = jsonObj;
        res.render('AppBankFundTransferNewPage',parameter);
        console.log(parameter);        
    });
});

module.exports = router;
