var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppApOpeningBalanceRead',
    Heading:'AppApOpeningBalance',
    ApOpeningBalanceId:{label :'ApOpeningBalanceId'},
    VendorId:{label :'VendorId'},
    Date:{label :'Date'},
    InvoiceNo:{label :'InvoiceNo'},
    PurchaseOrderNo:{label :'PurchaseOrderNo'},
    CurrencyId:{label :'CurrencyId'},
    PayableValue:{label :'PayableValue'},
    TermOfPaymentId:{label :'TermOfPaymentId'},
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readAppApOpeningBalance?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readAppApOpeningBalance = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readAppApOpeningBalance(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchAppApOpeningBalance');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('AppApOpeningBalanceReadPage',parameter);
    }    
    Promise.all([readAppApOpeningBalance]).then(sendResult);
});

module.exports = router;
