var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppArAgingRead',
    Heading:'AppArAging',
    ArAgingId:{label :'ArAgingId'},
    CustomerId:{label :'CustomerId'},
    ReferenceNo:{label :'ReferenceNo'},
    Debit:{label :'Debit'},
    Credit:{label :'Credit'},
    ArDueDate:{label :'ArDueDate'},
    CurrentDate:{label :'CurrentDate'},
    Age:{label :'Age'},
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readAppArAging?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readAppArAging = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readAppArAging(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchAppArAging');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('AppArAgingReadPage',parameter);
    }    
    Promise.all([readAppArAging]).then(sendResult);
});

module.exports = router;
