var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppArCustomerDetailRead',
    Heading:'AppArCustomerDetail',
    ArCustomerDetailId:{label :'ArCustomerDetailId'},
    CustomerId:{label :'CustomerId'},
    Date:{label :'Date'},
    Note:{label :'Note'},
    Debit:{label :'Debit'},
    Credit:{label :'Credit'},
    DebitBalance:{label :'DebitBalance'},
    CreditBalance:{label :'CreditBalance'},
    Balance:{label :'Balance'},
    ReferenceNo:{label :'ReferenceNo'},
    ArDueDate:{label :'ArDueDate'},
    Status:{label :'Status'},
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readAppArCustomerDetail?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readAppArCustomerDetail = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readAppArCustomerDetail(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchAppArCustomerDetail');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('AppArCustomerDetailReadPage',parameter);
    }    
    Promise.all([readAppArCustomerDetail]).then(sendResult);
});

module.exports = router;
