var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppBankWriteCheckRead',
    Heading:'AppBankWriteCheck',
    BankWriteCheckId:{label :'BankWriteCheckId'},
    AccountNo:{label :'AccountNo'},
    IsPostDatedCheck:{label :'IsPostDatedCheck'},
    ReceivedBy:{label :'ReceivedBy'},
    Date:{label :'Date'},
    CheckNo:{label :'CheckNo'},
    Amount:{label :'Amount'},
    Memo:{label :'Memo'},
    AppBankWcFa:{Heading:'AppBankWcFa',
        BankWcFaId:{header:'BankWcFaId'},
        BankWriteCheckId:{header:'BankWriteCheckId'},
        AccountNo:{header:'AccountNo'},
        DepartmentId:{header:'DepartmentId'},
        Amount:{header:'Amount'},
        JobOrderNo:{header:'JobOrderNo'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readAppBankWriteCheck?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readAppBankWriteCheck = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readAppBankWriteCheck(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchAppBankWriteCheck');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var appBankWcFa = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.searchAppBankWcFa(jsonObj,function(err,rows) {
                jsonObj.appBankWcFa = rows;
                resolve(jsonObj);
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('AppBankWriteCheckReadPage',parameter);
    }    
    Promise.all([readAppBankWriteCheck,appBankWcFa]).then(sendResult);
});

module.exports = router;
