var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppInventoryOpenBalanceRead',
    Heading:'AppInventoryOpenBalance',
    InventoryOpeningBalanceId:{label :'InventoryOpeningBalanceId'},
    LocationMaster:{label :'LocationMaster'},
    ProductId:{label :'ProductId'},
    BeginningBalance:{label :'BeginningBalance'},
    UnitCost:{label :'UnitCost'},
    IsSerialNumber:{label :'IsSerialNumber'},
    IsLotNumber:{label :'IsLotNumber'},
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readAppInventoryOpenBalance?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readAppInventoryOpenBalance = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readAppInventoryOpenBalance(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchAppInventoryOpenBalance');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('AppInventoryOpenBalanceReadPage',parameter);
    }    
    Promise.all([readAppInventoryOpenBalance]).then(sendResult);
});

module.exports = router;
