var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppRecurringBankCashRcvRead',
    Heading:'AppRecurringBankCashRcv',
    RecurringBankCashReceiveId:{label :'RecurringBankCashReceiveId'},
    TransactionName:{label :'TransactionName'},
    Frequency:{label :'Frequency'},
    AccountNo:{label :'AccountNo'},
    IsPostDatedCheck:{label :'IsPostDatedCheck'},
    From:{label :'From'},
    Date:{label :'Date'},
    ReferenceNo:{label :'ReferenceNo'},
    Amount:{label :'Amount'},
    Memo:{label :'Memo'},
    AppRecurringBankCrFa:{Heading:'AppRecurringBankCrFa',
        RecurringBankCrFaId:{header:'RecurringBankCrFaId'},
        RecurringBankCashReceiveId:{header:'RecurringBankCashReceiveId'},
        AccountNo:{header:'AccountNo'},
        DepartmentId:{header:'DepartmentId'},
        Amount:{header:'Amount'},
        JobOrderNo:{header:'JobOrderNo'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readAppRecurringBankCashRcv?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readAppRecurringBankCashRcv = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readAppRecurringBankCashRcv(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchAppRecurringBankCashRcv');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var appRecurringBankCrFa = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.searchAppRecurringBankCrFa(jsonObj,function(err,rows) {
                jsonObj.appRecurringBankCrFa = rows;
                resolve(jsonObj);
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('AppRecurringBankCashRcvReadPage',parameter);
    }    
    Promise.all([readAppRecurringBankCashRcv,appRecurringBankCrFa]).then(sendResult);
});

module.exports = router;
