var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppRecurringBankWriteCheckRead',
    Heading:'AppRecurringBankWriteCheck',
    RecurringBankWriteCheckId:{label :'RecurringBankWriteCheckId'},
    TransactionName:{label :'TransactionName'},
    Frequency:{label :'Frequency'},
    AccountNo:{label :'AccountNo'},
    IsPostDatedCheck:{label :'IsPostDatedCheck'},
    ReceivedBy:{label :'ReceivedBy'},
    Date:{label :'Date'},
    CheckNo:{label :'CheckNo'},
    Amount:{label :'Amount'},
    Memo:{label :'Memo'},
    AppRecurringBankWcFa:{Heading:'AppRecurringBankWcFa',
        RecurringBankWcFaId:{header:'RecurringBankWcFaId'},
        RecurringBankWriteCheckId:{header:'RecurringBankWriteCheckId'},
        AccountNo:{header:'AccountNo'},
        DepartmentId:{header:'DepartmentId'},
        Amount:{header:'Amount'},
        JobOrderNo:{header:'JobOrderNo'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readAppRecurringBankWriteCheck?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readAppRecurringBankWriteCheck = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readAppRecurringBankWriteCheck(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchAppRecurringBankWriteCheck');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var appRecurringBankWcFa = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.searchAppRecurringBankWcFa(jsonObj,function(err,rows) {
                jsonObj.appRecurringBankWcFa = rows;
                resolve(jsonObj);
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('AppRecurringBankWriteCheckReadPage',parameter);
    }    
    Promise.all([readAppRecurringBankWriteCheck,appRecurringBankWcFa]).then(sendResult);
});

module.exports = router;
