var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppRecurringJournalRead',
    Heading:'AppRecurringJournal',
    RecurringJournalId:{label :'RecurringJournalId'},
    TransactionName:{label :'TransactionName'},
    Frequency:{label :'Frequency'},
    Reference:{label :'Reference'},
    Date:{label :'Date'},
    Note:{label :'Note'},
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readAppRecurringJournal?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readAppRecurringJournal = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readAppRecurringJournal(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchAppRecurringJournal');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('AppRecurringJournalReadPage',parameter);
    }    
    Promise.all([readAppRecurringJournal]).then(sendResult);
});

module.exports = router;
