router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readAppRptProfitAndLoss?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readAppRptProfitAndLoss = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readAppRptProfitAndLoss(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchAppRptProfitAndLoss');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('AppRptProfitAndLossReadPage',parameter);
    }    
    Promise.all([readAppRptProfitAndLoss]).then(sendResult);
});

module.exports = router;
