var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppVendorTermOfPaymentRead',
    Heading:'AppVendorTermOfPayment',
    VendorId:{label :'VendorId'},
    TermOfPaymentId:{label :'TermOfPaymentId'},
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    jsonObj.rowid = 0;
    req.session.previouspath = 'readAppVendorTermOfPayment?'+req._parsedUrl.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
    }
    var readAppVendorTermOfPayment = new Promise((resolve, reject) => {
        setTimeout(() => {
            dao.readAppVendorTermOfPayment(jsonObj,function(err,rows) {
                if(err) res.status(err.code).end();
                else{
                    if(rows[0]===undefined) res.redirect('/searchAppVendorTermOfPayment');
                    else{ 
                        jsonObj = rows[0];
                        resolve(jsonObj);
                    }
                }
            });
        },1000);
    });
    
    var sendResult = (results) => {
        console.log(jsonObj)
        parameter.record = jsonObj;        
        res.render('AppVendorTermOfPaymentReadPage',parameter);
    }    
    Promise.all([readAppVendorTermOfPayment]).then(sendResult);
});

module.exports = router;
