var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppAccountClassSearch',
    Heading:'AppAccountClass',
    AccountClassId:{label :'AccountClassId'},
    ClassificationName:{label :'ClassificationName'},
    Desc:{label :'Desc'},
    AppAccountClass:{Heading:'AppAccountClass',
        AccountClassId:{header:'AccountClassId'},
        ClassificationName:{header:'ClassificationName'},
        Desc:{header:'Desc'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    req.session.previouspath = 'searchAppAccountClass?'+req._parsedUrl.query;
    dao.searchAppAccountClass(jsonObj,function(err,rows) {
        parameter = {
            session: req.session,
            cookies: req.cookies,
            resource: appresource,
            dateFormat: dateFormat,
            records: rows,
        }
        res.render('AppAccountClassSearchPage',parameter);
        console.log(parameter);        
    })
});

module.exports = router;
