var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppArAgingSearch',
    Heading:'AppArAging',
    ArAgingId:{label :'ArAgingId'},
    CustomerId:{label :'CustomerId'},
    ReferenceNo:{label :'ReferenceNo'},
    Debit:{label :'Debit'},
    Credit:{label :'Credit'},
    ArDueDate:{label :'ArDueDate'},
    CurrentDate:{label :'CurrentDate'},
    Age:{label :'Age'},
    AppArAging:{Heading:'AppArAging',
        ArAgingId:{header:'ArAgingId'},
        CustomerId:{header:'CustomerId'},
        ReferenceNo:{header:'ReferenceNo'},
        Debit:{header:'Debit'},
        Credit:{header:'Credit'},
        ArDueDate:{header:'ArDueDate'},
        CurrentDate:{header:'CurrentDate'},
        Age:{header:'Age'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    req.session.previouspath = 'searchAppArAging?'+req._parsedUrl.query;
    dao.searchAppArAging(jsonObj,function(err,rows) {
        parameter = {
            session: req.session,
            cookies: req.cookies,
            resource: appresource,
            dateFormat: dateFormat,
            records: rows,
        }
        res.render('AppArAgingSearchPage',parameter);
        console.log(parameter);        
    })
});

module.exports = router;
