var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppBankReconciliationDetailSearch',
    Heading:'AppBankReconciliationDetail',
    BankReconciliationDetailId:{label :'BankReconciliationDetailId'},
    BankReconciliationId:{label :'BankReconciliationId'},
    Date:{label :'Date'},
    ReferenceNo:{label :'ReferenceNo'},
    Note:{label :'Note'},
    DebitAmount:{label :'DebitAmount'},
    CreditAmount:{label :'CreditAmount'},
    IsSelected:{label :'IsSelected'},
    AppBankReconciliationDetail:{Heading:'AppBankReconciliationDetail',
        BankReconciliationDetailId:{header:'BankReconciliationDetailId'},
        BankReconciliationId:{header:'BankReconciliationId'},
        Date:{header:'Date'},
        ReferenceNo:{header:'ReferenceNo'},
        Note:{header:'Note'},
        DebitAmount:{header:'DebitAmount'},
        CreditAmount:{header:'CreditAmount'},
        IsSelected:{header:'IsSelected'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    req.session.previouspath = 'searchAppBankReconciliationDetail?'+req._parsedUrl.query;
    dao.searchAppBankReconciliationDetail(jsonObj,function(err,rows) {
        parameter = {
            session: req.session,
            cookies: req.cookies,
            resource: appresource,
            dateFormat: dateFormat,
            records: rows,
        }
        res.render('AppBankReconciliationDetailSearchPage',parameter);
        console.log(parameter);        
    })
});

module.exports = router;
