var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppCompanyInfoSearch',
    Heading:'AppCompanyInfo',
    CompanyInfoId:{label :'CompanyInfoId'},
    CompanyName:{label :'CompanyName'},
    AddressLine1:{label :'AddressLine1'},
    AddressLine2:{label :'AddressLine2'},
    CityId:{label :'CityId'},
    ProvinceId:{label :'ProvinceId'},
    CountryId:{label :'CountryId'},
    PostalCode:{label :'PostalCode'},
    AppCompanyInfo:{Heading:'AppCompanyInfo',
        CompanyInfoId:{header:'CompanyInfoId'},
        CompanyName:{header:'CompanyName'},
        AddressLine1:{header:'AddressLine1'},
        AddressLine2:{header:'AddressLine2'},
        CityId:{header:'CityId'},
        ProvinceId:{header:'ProvinceId'},
        CountryId:{header:'CountryId'},
        PostalCode:{header:'PostalCode'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    req.session.previouspath = 'searchAppCompanyInfo?'+req._parsedUrl.query;
    dao.searchAppCompanyInfo(jsonObj,function(err,rows) {
        parameter = {
            session: req.session,
            cookies: req.cookies,
            resource: appresource,
            dateFormat: dateFormat,
            records: rows,
        }
        res.render('AppCompanyInfoSearchPage',parameter);
        console.log(parameter);        
    })
});

module.exports = router;
