var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppCurrencySearch',
    Heading:'AppCurrency',
    CurrencyId:{label :'CurrencyId'},
    CurrencyCode:{label :'CurrencyCode'},
    CurrencyName:{label :'CurrencyName'},
    CurrencySymbol:{label :'CurrencySymbol'},
    ExchangeRate:{label :'ExchangeRate'},
    ByDate:{label :'ByDate'},
    ControlAccountAr:{label :'ControlAccountAr'},
    ControlAccountAp:{label :'ControlAccountAp'},
    ControlAccountBankPayment:{label :'ControlAccountBankPayment'},
    ControlAccountCashPayment:{label :'ControlAccountCashPayment'},
    IsCurrencyBase:{label :'IsCurrencyBase'},
    IsForeignCurrency:{label :'IsForeignCurrency'},
    AppCurrency:{Heading:'AppCurrency',
        CurrencyId:{header:'CurrencyId'},
        CurrencyCode:{header:'CurrencyCode'},
        CurrencyName:{header:'CurrencyName'},
        CurrencySymbol:{header:'CurrencySymbol'},
        ExchangeRate:{header:'ExchangeRate'},
        ByDate:{header:'ByDate'},
        ControlAccountAr:{header:'ControlAccountAr'},
        ControlAccountAp:{header:'ControlAccountAp'},
        ControlAccountBankPayment:{header:'ControlAccountBankPayment'},
        ControlAccountCashPayment:{header:'ControlAccountCashPayment'},
        IsCurrencyBase:{header:'IsCurrencyBase'},
        IsForeignCurrency:{header:'IsForeignCurrency'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    req.session.previouspath = 'searchAppCurrency?'+req._parsedUrl.query;
    dao.searchAppCurrency(jsonObj,function(err,rows) {
        parameter = {
            session: req.session,
            cookies: req.cookies,
            resource: appresource,
            dateFormat: dateFormat,
            records: rows,
        }
        res.render('AppCurrencySearchPage',parameter);
        console.log(parameter);        
    })
});

module.exports = router;
