var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppJobTitleSearch',
    Heading:'AppJobTitle',
    JobTitleId:{label :'JobTitleId'},
    JobTitleCode:{label :'JobTitleCode'},
    JobDescription:{label :'JobDescription'},
    JobSpecification:{label :'JobSpecification'},
    AppJobTitle:{Heading:'AppJobTitle',
        JobTitleId:{header:'JobTitleId'},
        JobTitleCode:{header:'JobTitleCode'},
        JobDescription:{header:'JobDescription'},
        JobSpecification:{header:'JobSpecification'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    req.session.previouspath = 'searchAppJobTitle?'+req._parsedUrl.query;
    dao.searchAppJobTitle(jsonObj,function(err,rows) {
        parameter = {
            session: req.session,
            cookies: req.cookies,
            resource: appresource,
            dateFormat: dateFormat,
            records: rows,
        }
        res.render('AppJobTitleSearchPage',parameter);
        console.log(parameter);        
    })
});

module.exports = router;
