var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppRecurringJournalSearch',
    Heading:'AppRecurringJournal',
    RecurringJournalId:{label :'RecurringJournalId'},
    TransactionName:{label :'TransactionName'},
    Frequency:{label :'Frequency'},
    Reference:{label :'Reference'},
    Date:{label :'Date'},
    Note:{label :'Note'},
    AppRecurringJournal:{Heading:'AppRecurringJournal',
        RecurringJournalId:{header:'RecurringJournalId'},
        TransactionName:{header:'TransactionName'},
        Frequency:{header:'Frequency'},
        Reference:{header:'Reference'},
        Date:{header:'Date'},
        Note:{header:'Note'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    req.session.previouspath = 'searchAppRecurringJournal?'+req._parsedUrl.query;
    dao.searchAppRecurringJournal(jsonObj,function(err,rows) {
        parameter = {
            session: req.session,
            cookies: req.cookies,
            resource: appresource,
            dateFormat: dateFormat,
            records: rows,
        }
        res.render('AppRecurringJournalSearchPage',parameter);
        console.log(parameter);        
    })
});

module.exports = router;
