var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppRptCashFlowSearch',
    Heading:'AppRptCashFlow',
    RptCashFlowId:{label :'RptCashFlowId'},
    RptCashFlowName:{label :'RptCashFlowName'},
    DateFrom:{label :'DateFrom'},
    DateUntil:{label :'DateUntil'},
    AccountNo:{label :'AccountNo'},
    EndingBalanceDebit:{label :'EndingBalanceDebit'},
    EndingBalanceCredit:{label :'EndingBalanceCredit'},
    DateCreated:{label :'DateCreated'},
    CreatedBy:{label :'CreatedBy'},
    AppRptCashFlow:{Heading:'AppRptCashFlow',
        RptCashFlowId:{header:'RptCashFlowId'},
        RptCashFlowName:{header:'RptCashFlowName'},
        DateFrom:{header:'DateFrom'},
        DateUntil:{header:'DateUntil'},
        AccountNo:{header:'AccountNo'},
        EndingBalanceDebit:{header:'EndingBalanceDebit'},
        EndingBalanceCredit:{header:'EndingBalanceCredit'},
        DateCreated:{header:'DateCreated'},
        CreatedBy:{header:'CreatedBy'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    req.session.previouspath = 'searchAppRptCashFlow?'+req._parsedUrl.query;
    dao.searchAppRptCashFlow(jsonObj,function(err,rows) {
        parameter = {
            session: req.session,
            cookies: req.cookies,
            resource: appresource,
            dateFormat: dateFormat,
            records: rows,
        }
        res.render('AppRptCashFlowSearchPage',parameter);
        console.log(parameter);        
    })
});

module.exports = router;
