var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppVendorTermOfPaymentSearch',
    Heading:'AppVendorTermOfPayment',
    VendorId:{label :'VendorId'},
    TermOfPaymentId:{label :'TermOfPaymentId'},
    AppVendorTermOfPayment:{Heading:'AppVendorTermOfPayment',
        VendorId:{header:'VendorId'},
        TermOfPaymentId:{header:'TermOfPaymentId'},
        },
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    req.session.previouspath = 'searchAppVendorTermOfPayment?'+req._parsedUrl.query;
    dao.searchAppVendorTermOfPayment(jsonObj,function(err,rows) {
        parameter = {
            session: req.session,
            cookies: req.cookies,
            resource: appresource,
            dateFormat: dateFormat,
            records: rows,
        }
        res.render('AppVendorTermOfPaymentSearchPage',parameter);
        console.log(parameter);        
    })
});

module.exports = router;
