var express = require('express');
var router = express.Router();
var dateFormat = require('dateformat');    
var database = require('./dao');
var dao = new database.Dao();    
var sessionChecker = require('./security');
var appresource = {
    Title:'AppApVendorDetailWrite',
    Heading:'AppApVendorDetail',
    ApVendorDetailId:{label :'ApVendorDetailId'},
    VendorId:{label :'VendorId'},
    Date:{label :'Date'},
    Note:{label :'Note'},
    Debit:{label :'Debit'},
    Credit:{label :'Credit'},
    DebitBalance:{label :'DebitBalance'},
    CreditBalance:{label :'CreditBalance'},
    Balance:{label :'Balance'},
    ReferenceNo:{label :'ReferenceNo'},
    ArDueDate:{label :'ArDueDate'},
    Status:{label :'Status'},
    }

router.get('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.query;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,
        statusCode:100,
        statusMessage:'',
    }
    dao.readAppApVendorDetail(jsonObj,function(err,rows) {
        parameter.record = rows[0];
        res.render('AppApVendorDetailWritePage',parameter);
        console.log(parameter);        
    });
});
router.post('/', sessionChecker, function(req, res, next) {
    var jsonObj = req.body;
    parameter = {
        session: req.session,
        cookies: req.cookies,
        resource: appresource,
        dateFormat: dateFormat,        
    }
    dao.writeAppApVendorDetail(jsonObj,function(err,rows) {    
        if(err==null){
            parameter.statusCode = 200;
            parameter.statusMessage = 'Successfully created';
        }else{
            parameter.statusCode = err.code;
            parameter.statusMessage = err.message;
        }
        parameter.record = jsonObj;
        res.render('AppApVendorDetailWritePage',parameter);
        console.log(parameter);        
    });
});

module.exports = router;
