var express = require('express');
var router = express.Router();
var database = require('./dao');
var dao = new database.Dao();
        
/**
 * @swagger
 * /searchAppPayGrade:
 *   get:
 *     tags:
 *       - Administration
 *     description: searchAppPayGrade
 *     summary: AppPayGradeSearch
 *     consumes:
 *       - application/json
 *     produces:
 *       - application/json
 *     parameters: 
 *       - name: payGradeId
 *         description: Long
 *         in: query
 *         type: Long
 *       - name: payGradeName
 *         description: String
 *         in: query
 *         type: String
 *       - name: minSalary
 *         description: Double
 *         in: query
 *         type: Double
 *       - name: maxSalary
 *         description: Double
 *         in: query
 *         type: Double
 *       - name: currency
 *         description: String
 *         in: query
 *         type: String
 *     responses:
 *       '200':
 *         description: OK
 *       '401':
 *         description: Unauthorized
 *       '403':
 *         description: Forbidden
 *       '404':
 *         description: Not Found 
 *       '500':
 *         description: Internal Server Problem
 */    

router.get('/', function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    dao.searchAppPayGrade(jsonObj,function(err,rows) {    
        if(err) res.status(err.code).end();
        else {
            jsonObj.appPayGrade = rows;
            res.status(200).send(jsonObj);
        }
        console.log(jsonObj);        
    });
});

module.exports = router;
